package com.chainsys.switching;

public class SwitchDemo {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
       int day=2;
       switch(day)
       {
       case 1:
    	   System.out.println("Monday");
    	   break;
       case 2:
    	   System.out.println("Tuesday");
       case 3:
    	   System.out.println("Wednesday");
       default:
    	   break;
       }
	}

}
